package week1.day2;

public class Evennumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int data =22;
if(data%2==0) {
	System.out.println("Even number "+ data);
}else if (data%2!=1){
		System.out.println("Odd number "+ data);
	}else {
		System.out.println("Neutral number" + data);
	}
}
}